#!/usr/bin/env python3# -*- coding: utf-8 -*-"""数据可视化"""import pandas as pdimport numpy as npimport matplotlib.pyplot as pltdataset_csv='houses_data_1.csv'#散点图def plot_line_chart():    df=pd.read_csv(dataset_csv)    X=df.iloc[:, 5].values #土地面积    y=df.iloc[:, 4].values #价格    print(X.size,y.size)        #X_grid = np.arange(min(X), max(X), 0.01)    #X_grid = X_grid.reshape((len(X_grid), 1))    plt.scatter(X, y, color = 'blue')    plt.title('Line Chart')    plt.xlabel('Land Area')    plt.ylabel('Price')    plt.show()    #直方图def plot_histogram_chart():    df=pd.read_csv(dataset_csv)    #X=df.iloc[:, 5].values #土地面积    #y=df.iloc[:, 4].values #价格#统计某列的区间,绘制直方图df=pd.read_csv(dataset_csv)X_df =df.price #land_area,price,nearest_stationprint(X_df.max())current_num=0step=1000x=[]y=[]while current_num<= X_df.max():    counter=0    for i in X_df:        if current_num<= i <(current_num+step):            counter+=1    x.append(str(current_num)+'~'+str(current_num+step-1))    y.append(counter)    current_num+=stepprint(x,y)plt.bar(x, y,color='#f5dbba')plt.title('Statistics')plt.xlabel('X')plt.ylabel('Count')plt.show()   